import React, { useState, useEffect } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Routes, Route } from "react-router-dom";
import Home from "./routes/home/home.component";
import Locations from "./routes/locations/locations.component";
import Preloader from "./routes/preloader/preloader.component"; // import the Preloader component

const MainComponent = () => {
  const [loaded, setLoaded] = useState(false);

  const handleAllImagesLoaded = () => {
    setLoaded(true);
  };

  return (
    <>
      {!loaded && (
        <div className="preloader">
          <div className="preloader-progress">{50}%</div>
        </div>
      )}
      <div
        className="main-component"
        style={{
          height: "100vh",
          width: "100%",
          position: "relative",
          zIndex: 1,
        }}
      >
        <Home onLoaded={handleAllImagesLoaded} />
      </div>
    </>
  );
};
const ParallaxComponent = ({ speed, color, zIndex }) => {
  const divStyle = { backgroundColor: color };
  return (
    <ParallaxLayer speed={speed} offset={1} style={{ zIndex }}>
      <div
        style={{ ...divStyle, boxShadow: "80px 80px 10px rgba(0, 0, 0, 1)" }}
      >
        <Locations />
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  const [isLoading, setIsLoading] = useState(true); // add the isLoading state

  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        position: "relative",
      }}
    >
      {isLoading ? <Preloader /> : null}
      <Parallax
        pages={3}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 2,
        }}
      >
        <ParallaxComponent speed={0.05} color="yellow" zIndex={3} />
      </Parallax>
      <MainComponent isLoading={setIsLoading} />
    </div>
  );
};

export default App;
