import React, { useEffect } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Routes, Route } from "react-router-dom";
import Home from "./routes/home/home.component";
import Locations from "./routes/locations/locations.component";

const MainComponent = () => {
  return (
    <div className="main-component">
      <img src="https://cdn.discordapp.com/attachments/1065884142417498145/1066675467492532264/hana-bg-final.png" />
      <h1>Main Component</h1>
    </div>
  );
};

const ParallaxComponent = ({ speed, color }) => {
  const divStyle = { backgroundColor: color };
  return (
    <ParallaxLayer speed={speed} offset={0.9}>
      <div style={divStyle}>
        <h2>Parallax Component</h2>
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  useEffect(() => {
    // Add the "overflow: hidden" property to the body element when the component mounts
    document.body.style.overflow = "hidden";

    // Remove the "overflow: hidden" property from the body element when the component unmounts
    return () => {
      document.body.style.overflow = "auto";
    };
  }, []);
  return (
    <Parallax pages={1}>
      <MainComponent
        style={{ position: "fixed", height: "100%", width: "100%" }}
      />
      <ParallaxComponent speed={0.5} color="yellow" />
      <ParallaxComponent speed={0.75} color="purple" />
      <ParallaxComponent speed={1} color="green" />
    </Parallax>
  );
};

export default App;
