import React, { useEffect } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Routes, Route } from "react-router-dom";
import Home from "./routes/home/home.component";
import Locations from "./routes/locations/locations.component";

const MainComponent = () => {
  return (
    <div
      className="main-component"
      style={{
        height: "100vh",
        width: "100%",
        position: "relative",
        zIndex: 2, // set the z-index to a higher value than the parallax components
        pointerEvents: "auto", // allow mouse events to be detected by the MainComponent
      }}
    >
      <button onClick={() => this.parallax.scrollTo(2)}>
        Go to ParallaxComponent 3
      </button>
      <Home />
    </div>
  );
};

const ParallaxComponent = ({ speed, color, zIndex }) => {
  const divStyle = { backgroundColor: color };
  return (
    <ParallaxLayer speed={speed} offset={0.7} style={{ zIndex }}>
      <div style={divStyle}>
        <h2>Parallax Component</h2>
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        height: "100%",
        width: "100%",
      }}
    >
      <Parallax
        pages={3}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 1,
          backgroundColor: "transparent",
        }}
      >
        <ParallaxComponent speed={0.5} color="yellow" zIndex={2} />
        <ParallaxComponent speed={0.75} color="purple" zIndex={3} />
        <ParallaxComponent speed={1} color="green" zIndex={4} />
      </Parallax>
      <MainComponent />
    </div>
  );
};

export default App;
