import React, { useRef } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";

const MainComponent = ({ scrollHandler }) => {
  return (
    <div className="main-component">
      <button onClick={() => scrollHandler(0)}>
        Go to Parallax Component 1
      </button>
      <button onClick={() => scrollHandler(1)}>
        Go to Parallax Component 2
      </button>
      <button onClick={() => scrollHandler(2)}>
        Go to Parallax Component 3
      </button>
    </div>
  );
};

const ParallaxComponent = ({ speed, color, zIndex }) => {
  const divStyle = {
    backgroundColor: color,
    boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.25)",
  };
  return (
    <ParallaxLayer speed={speed} offset={0.7} style={{ zIndex }}>
      <div style={divStyle}>
        <h2>Parallax Component</h2>
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  const parallaxRef = useRef(null);

  const scrollHandler = (pageIndex) => {
    parallaxRef.current.scrollTo(pageIndex);
  };

  return (
    <div className="App">
      <MainComponent scrollHandler={scrollHandler} />
      <Parallax
        pages={3}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 2,
        }}
        ref={parallaxRef}
      >
        <ParallaxComponent speed={0.5} color="yellow" />
        <ParallaxComponent speed={0.75} color="purple" />
        <ParallaxComponent speed={1} color="green" />
      </Parallax>
    </div>
  );
};

export default App;
