import React, { useEffect } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Routes, Route } from "react-router-dom";
import Home from "./routes/home/home.component";
import Locations from "./routes/locations/locations.component";

const MainComponent = () => {
  return (
    <div
      className="main-component"
      style={{
        height: "100vh",
        width: "100%",
        position: "relative",
        zIndex: 2, // set the z-index to a higher value than the parallax components
      }}
    >
      <Home />
    </div>
  );
};

const ParallaxComponent = ({ speed, color }) => {
  const divStyle = { backgroundColor: color };
  return (
    <ParallaxLayer speed={speed} offset={0.9}>
      <div style={divStyle}>
        <h2>Parallax Component</h2>
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        height: "100%",
        width: "100%",
      }}
    >
      <MainComponent />

      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
        }}
      >
        <Parallax pages={3}>
          <ParallaxComponent speed={0.5} color="yellow" />
          <ParallaxComponent speed={0.75} color="purple" />
          <ParallaxComponent speed={1} color="green" />
        </Parallax>
      </div>
    </div>
  );
};

export default App;
