import React, { useState, useEffect } from "react";
import "./App.css";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Routes, Route } from "react-router-dom";
import Home from "./routes/home/home.component";
import Locations from "./routes/locations/locations.component";
import Preloader from "./routes/preloader/preloader.component"; // import the Preloader component

const MainComponent = () => {
  return (
    <div
      className="main-component"
      style={{
        height: "100vh",
        width: "100%",
        position: "relative",
        zIndex: 1, // set the z-index to a lower value than the parallax components
      }}
    >
      <Home />
    </div>
  );
};

const ParallaxComponent = ({ speed }) => {
  return (
    <ParallaxLayer speed={speed} offset={1}>
      <div
        style={{
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Locations />
      </div>
    </ParallaxLayer>
  );
};

const App = () => {
  return (
    <div
      style={{
        height: "100%",
        width: "100%",
        position: "relative",
      }}
    >
      <Parallax
        pages={3}
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 10,
          pointerEvents: "none", // add this line to allow pointer events to pass through
        }}
      >
        <ParallaxComponent speed={0.5} />
      </Parallax>
      <MainComponent />
    </div>
  );
};

export default App;
